import React, { Component } from 'react';
import {
  BrowserRouter as Router,
  Link,
  Route,
  Switch,
} from 'react-router-dom';
import Weather from './Weather.js';




// class NavBar extends Component{
const NavBar = (props)=>{

	// render(){
		console.log("********")
		console.log(props);

		return(

		<div>
		{props.country&&props.city&& <p>


			Location: 
		<span className='weather_value'>{props.city},{props.country}</span></p>}


		{props.temp&&<p>Temperature:
		<span className='weather_value'>Current: {props.temp}F / Max: {props.temp_max}F / Min: {props.temp_min}F </span></p>}


		{props.humid&& <p>HUMMMMMMMMMMM:
		<span className='weather_value'> {props.humid}%</span></p>}


		{props.desc && <p>Current Conditions:
		<span className='weather_value'> {props.desc}</span></p>}



		{props.tomorrow&&<p>Tomorrow:
		<span className='weather_value'> Max Temp : {props.tomorrow}F / Min Temp : {this.props.tomorrowMin}F / Conditions : {this.props.tomorrowCon} </span></p>}

		{props.tomorrow2&&<p>Tomorrow + 1:
		<span className='weather_value'> Max Temp : {props.tomorrow2}F / Min Temp : {this.props.tomorrowMin2}F / Conditions : {this.props.tomorrowCon2} </span></p>}

		{props.tomorrow3&&<p>Tomorrow + 2:
		<span className='weather_value'> Max Temp : {props.tomorrow3}F / Min Temp : {this.props.tomorrowMin3}F / Conditions : {this.props.tomorrowCon3} </span></p>}


		{props.notes&& <p>{props.notes}</p>}


  		</div>

			)
	}
// }


export default NavBar;
