import React from 'react';
import { Link } from 'react-router-dom';
import NavBar from './NavBar.js';



// const Weather = (props)=>{
class Weather extends React.Component{

	// var style1 = {
	// 	color:'red',
	// 	fontSize: '20px'
	// }

	// console.log(props);
	render(){

	return(

		<div className = 'weather-info'>
<ul>
        <Link to='/'>Loc#1     </Link>
        <Link to='/news' onClick = {this.props.loadWeather}>Loc#2    </Link>
        <Link to='/news'>Loc#3    </Link>

      </ul>	
		{this.props.country&&this.props.city&& <p>


			Location: 
		<span className='weather_value'>{this.props.city},{this.props.country}</span></p>}


		{this.props.temp&&<p>Temperature:
		<span className='weather_value'>Current: {this.props.temp}F / Max: {this.props.temp_max}F / Min: {this.props.temp_min}F </span></p>}


		{this.props.humid&& <p>Humidity:
		<span className='weather_value'> {this.props.humid}%</span></p>}


		{this.props.desc && <p>Current Conditions:
		<span className='weather_value'> {this.props.desc}</span></p>}



		{this.props.tomorrow&&<p>Tomorrow:
		<span className='weather_value'> Max Temp : {this.props.tomorrow}F / Min Temp : {this.props.tomorrowMin}F / Conditions : {this.props.tomorrowCon} </span></p>}

		{this.props.tomorrow2&&<p>Tomorrow + 1:
		<span className='weather_value'> Max Temp : {this.props.tomorrow2}F / Min Temp : {this.props.tomorrowMin2}F / Conditions : {this.props.tomorrowCon2} </span></p>}

		{this.props.tomorrow3&&<p>Tomorrow + 2:
		<span className='weather_value'> Max Temp : {this.props.tomorrow3}F / Min Temp : {this.props.tomorrowMin3}F / Conditions : {this.props.tomorrowCon3} </span></p>}


		{this.props.notes&& <p>{this.props.notes}</p>}




		</div>

		)

	console.log("XXXXXXXXXXX");
	console.log(this.props);
	console.log("XXXXXXXXXXX");
}
}

export default Weather;
