import React from 'react';

const Weather = (props)=>{

	var style1 = {
		color:'red',
		fontSize: '20px'
	}
	return(
		<div className = 'weather-info'  style={style1}>
		{props.country&&props.city&& <p>Location: 
		<span className='weather_value'>{props.city},{props.country}</span></p>}


		{props.temp&&<p>Current Temp:
		<span className='weather_value'> {props.temp}</span></p>}


		{props.humid&& <p>Humidity:
		<span className='weather_value'> {props.humid}</span></p>}


		{props.desc && <p>Current Conditions:
		<span className='weather_value'> {props.desc}</span></p>}



		{props.tomorrow&&<p>Tomorrow:
		<span className='weather_value'> Max Temp : {props.tomorrow}F / Min Temp : {props.tomorrow}F</span></p>}

		{props.tomorrow2&&<p>Tomorrow + 1:
		<span className='weather_value'> {props.tomorrow2}</span></p>}

		{props.tomorrow3&&<p>Tomorrow + 2:
		<span className='weather_value'> {props.tomorrow3}</span></p>}


		{props.notes&& <p>{props.notes}</p>}


		</div>
		)
}

export default Weather;
