import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import Header from './Header.js';
import Input from './Input.js';
import Weather from './Weather.js';
const apiKey = `d97e07323bb27edb864d550f337de5a6`;


class App extends Component {

  state = {
    city: undefined,
    country: undefined,
    temp: undefined,
    humid: undefined,
    desc: undefined,
    notes: undefined
  }

  getWeather = async (event) =>{


    event.preventDefault();

    const city = event.target.elements.city.value;
    const country = event.target.elements.country.value;

    // const apiWeather = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&units=imperial&appid=${apiKey}`);
    const apiWeather = await fetch(
      `https://query.yahooapis.com/v1/public/yql?q=select%20*%20from%20weather.forecast%20where%20woeid%20in%20(select%20woeid%20from%20geo.places(1)%20where%20text%3D%22${
        this.state.input
      }%22)%20and%20u%3D'c'&format=json&env=store%3A%2F%2Fdatatables.org%2Falltableswithkeys`
    )
    const results = await apiWeather.json();

    if(city && country){

    this.setState({
      // city: results.name,
      city: results.query.results.channel.location.city,
      // country: results.sys.country,
      // temp: results.main.temp,
      // humid: results.main.humidity,
      // desc: results.weather[0].description,
      // notes: ""

    })
    }else{
      this.setState({
      notes: "Input values required!"  
      })
    }


    console.log("888888*********************results");
    console.log(results);
    console.log("888888*********************results");

  }

  render() {
    return (
      <div>
        <div className="wrapper">
          <div className='main'>
            <div className='container'>
              <div className='row'>
                <div className = 'col-xs-5 title-container'>
            <Header/>
                </div>
                <div className = 'col-xs-7 form-container'>
            <Input loadWeather={this.getWeather}/>
            <Weather
            city= {this.state.city}
            country= {this.state.country}
            temp= {this.state.temp}
            humid= {this.state.humid}
            desc= {this.state.desc}
            notes= {this.state.notes}
            />
                </div>
              </div>
            </div>
          </div>
        </div>


      </div>
    );
  }
}

export default App;
